# -*- coding: utf-8 -*-
import os
import sys
import xbmcaddon
import six

ADDON = xbmcaddon.Addon()
ADDON_ID = ADDON.getAddonInfo("id")
ADDON_NAME = ADDON.getAddonInfo("name")
ADDON_ICON = six.ensure_text(ADDON.getAddonInfo("icon"))
try:
    ADDON_PATH = os.path.abspath(
        os.path.join(
            os.path.dirname(
                six.ensure_text(os.path.realpath(__file__), sys.getfilesystemencoding(), 'ignore')
            ),
            "..",
            "..",
            ".."
        )
    )
except Exception:
    ADDON_PATH = ADDON.getAddonInfo("path")
